INSERT INTO Software (Id, ProdutoSoftware, Nome, Situacao, Descricao, Segmento, Tipo, LicencaSoftware, Tecnologia, Descontinuado, EntrarCatalogo, ContratoId) VALUES('03693087-F313-4F3A-9EB4-4D0B993540E7', 'ADMAcesso', 'ADMAcesso', 'Ativo', 'Responsável por centralizar e auditar a manutenção de todos os perfis e usuários que utilizam a relação de sistemas informada acima. Nesse sentido, o ADMACESSO efetua o cadastramento e controle desses sistemas, bem como as respectivas transações e funções existentes para que seja definido o perfil dos diversos usuários. Além disso, efetua, também, o cadastro de informações das empresas que estejam utilizando o Sistema.  ', 'Meios de Pagamentos', 'Desktop', 'Não', 'Delphi 7 e 10 Seattle 10', '0001-01-01 00:00:00', 'Sim', NULL);
INSERT INTO Software (Id, ProdutoSoftware, Nome, Situacao, Descricao, Segmento, Tipo, LicencaSoftware, Tecnologia, Descontinuado, EntrarCatalogo, ContratoId) VALUES('0D8D1688-3BA5-4605-96E9-AB7CF06586D3', 'ADMCCF', 'ADMCCF (Desktop)', 'Ativo', 'Sistema de Controle do Cadastro de Emitentes de Cheques sem Fundos), por sua vez, tem a finalidade de controlar a inclusão e a exclusão de clientes no CCF (Cadastro de Emitentes de Cheques sem Fundos) para cheques devolvidos de quaisquer dos Bancos integrantes do Sistema de Compensação Brasileiro. Efetua controles de recebimento e geração de arquivos, processamento de carga e geração de arquivos contendo os cheques incluídos e excluídos, realiza consulta de correntistas que possuem restrição na Instituição Financeira, inclusive informando se determinado correntista possui cheques de outros Bancos. Possui interface via Intranet para inclusão de informações pelas Agências. Possui rotina de recebimentos de cheques a excluir via interface com o Sistema de Automação Bancária, possui rotina de remessa do arquivo CCF607 para o Banco do Brasil contendo a movimentação de inclusões e exclusões efetuadas diariamente, possui rotina de recebimento do arquivo CCF617 — arquivo de retorno do Banco do Brasil com os cheques aceitos para serem incluídos no Serasa ou, ainda, excluídos. Possui também rotina de recebimento do arquivo CCF627 — retorno do Banco do Brasil referente aos registros de inclusões e exclusões de cheques rejeitados que tenham sido enviados no dia anterior através do processamento da remessa CCF607.', 'Meios de Pagamentos', 'Desktop', 'Não', 'Delphi 7 e 10 Seattle 10', '0001-01-01 00:00:00', 'Sim', NULL);
INSERT INTO Software (Id, ProdutoSoftware, Nome, Situacao, Descricao, Segmento, Tipo, LicencaSoftware, Tecnologia, Descontinuado, EntrarCatalogo, ContratoId) VALUES('1E5187D1-D2A3-4259-9267-1BC4595C2BD3', 'AdmCombustível', 'AdmCombustível', 'Descontinuado', 'Consiste em um sistema de controle de vendas de combustíveis para abastecimento de Ativos (Automóvel, Embarcação, Avião) de Prefeituras e empresas privadas via Rede BCard (rede de cartão de crédito e débito em estabelecimentos comerciais conveniados com o Banpará), possuindo interfaces com tais entidades e com a Solução de Internet Banking do Banpará para recepção de informações sobre veículos e limites, além de interfaces com o Sistema de Cartões, com o Sistema de Automação Bancária para verificação de saldo, com o Sistema de Contas Correntes para Prestação de Contas e com a Solução de Internet Banking do Banpará direcionada especificamente ao produto Cartão Combustível (IBCC). ', 'Meios de Pagamentos', 'Desktop', 'Não', 'Delphi 7 e 10 Seattle 10', '0001-01-01 00:00:00', 'Não', NULL);
INSERT INTO Software (Id, ProdutoSoftware, Nome, Situacao, Descricao, Segmento, Tipo, LicencaSoftware, Tecnologia, Descontinuado, EntrarCatalogo, ContratoId) VALUES('1047E8B4-DD1A-4CAF-9D02-BDF03BB357D2', 'APACAM', 'APACAM', 'Descontinuado', 'Instalado na Associação Paraense de Proteção ao Câncer de Mama. Semelhante ao MS CLIENTE, Enviando arquivo para o Banpará.', 'Meios de Pagamentos', 'Desktop', 'Não', 'Delphi 7 e 10 Seattle 10', '0001-01-01 00:00:00', 'Não', NULL);
